mapper.select:
select * from ( select pt1.id, pt1.parent_id, pt1.type_code, pt1.status, pt1.create_date, pt1.level , pt1.type_name from t_danger_type as pt1 where pt1.is_delete=0 and pt1.status=0 and pt1.level=1
AND pt1.id in (
#{typeId}
)
union select pt1.id, pt1.parent_id, pt1.type_code, pt1.status, pt1.create_date, pt1.level , concat((select pt2.type_name from t_danger_type as pt2 where pt2.id=pt1.parent_id), '-', pt1.type_name) as type_name from t_danger_type as pt1 where pt1.is_delete=0 and pt1.status=0 and pt1.level=2
AND pt1.parent_id in (
#{typeId}
)
) as pt3

select count(1) from t_danger_type
where type_name=#{typeName} and is_delete=0 and level=#{level} and parent_id=#{parentId}

    and id!=#{id}

limit 1

mapper.update:
update t_danger_type set
is_delete=1, update_by=#{userId}
where id=#{id} and is_delete=0

mapper.select:
select distinct d.id, d.no, d.region_id, d.line, d.type_id, dt.type_name, (case when r2.name is null then r.name else concat(r2.name,'-',r.name) end) as region_name, d.address, d.content, d.before_pic, d.rectify_term, r.type_id as region_type_id, d.check_user_id, ue.real_name as check_user_name, d.check_time, d.create_date, d.device, d.num, d.unit, d.status from t_danger as d left join sys_user_ext as ue on d.check_user_id=ue.user_id and ue.status=0 and ue.is_delete=0 left join t_region as r on r.id=d.region_id and r.is_delete=0 and r.status=0 left join t_region as r2 on r2.id=r.parent_id and r2.is_delete=0 and r2.status=0 left join t_danger_type as dt on dt.id=d.type_id and dt.is_delete=0 where d.is_delete=0
and d.id not in (select du.danger_id from t_danger_user as du on du.user_id=#{userId})
and d.region_id=#{regionId}
and d.status=#{status}
and (d.no like concat('%', #{searchKey}, '%') or d.content like concat('%', #{searchKey}, '%') or d.num like concat('%', #{searchKey}, '%') or d.unit like concat('%', #{searchKey}, '%') or d.address like concat('%', #{searchKey}, '%') or ue.real_name like concat('%', #{searchKey}, '%') or d.device like concat('%', #{searchKey}, '%') or d.line like concat('%', #{searchKey}, '%'))
group by d.id order by d.create_date desc

mapper.update:
update t_danger set
is_delete=1, update_by=#{createBy}, update_date=now()
where id=#{id} and is_delete=0;
update t_danger_rectify set
is_delete=1, update_by=#{createBy}, update_date=now()
where danger_id=#{id} and is_delete=0;

mapper.select:
select distinct d.id, d.no, d.region_id, d.line, d.type_id, dt.type_name, (case when r2.name is null then r.name else concat(r2.name,'-',r.name) end) as region_name, d.address, d.content, d.before_pic, d.rectify_term, r.type_id as region_type_id, d.check_user_id, ue.real_name as check_user_name, d.check_time, d.create_date, d.device, d.num, d.unit, d.status from t_danger as d left join sys_user_ext as ue on d.check_user_id=ue.user_id and ue.status=0 and ue.is_delete=0 left join t_region as r on r.id=d.region_id and r.is_delete=0 and r.status=0 left join t_region as r2 on r2.id=r.parent_id and r2.is_delete=0 and r2.status=0 left join t_danger_type as dt on dt.id=d.type_id and dt.is_delete=0 where d.is_delete=0
and d.id not in (select du.danger_id from t_danger_user as du on du.user_id=#{userId})
and d.region_id=#{regionId}
and d.status=#{status}
and (d.no like concat('%', #{searchKey}, '%') or d.content like concat('%', #{searchKey}, '%') or d.num like concat('%', #{searchKey}, '%') or d.unit like concat('%', #{searchKey}, '%') or d.address like concat('%', #{searchKey}, '%') or ue.real_name like concat('%', #{searchKey}, '%') or d.device like concat('%', #{searchKey}, '%') or d.line like concat('%', #{searchKey}, '%'))
group by d.id order by d.create_date desc
