mapper.select:
select id, device_name, type, device_code, push_url, status, is_conn, is_live, remark, create_date from t_device where is_delete=0 and type=1
and status=#{status}
order by create_date desc

select count(1) from t_device
where device_name=#{deviceName} and type=#{type}

    and id!=#{id}

limit 1

mapper.update:
update t_device set
is_delete=1, update_by=#{userId}
where id=#{id} and is_delete=0
